import { Behavior } from '../behavior';
import toggleFormInput from '../actions/toggle-form-input';

function toggle(event) {
  event.preventDefault();
  toggleFormInput(this);
}

export class Password extends Behavior {
  constructor(prefix, options) {
    const LINK = `.${prefix}-show_password, .${prefix}-show_multipassword`;

    super({
      ['click']: {
        [LINK]: toggle
      }
    });
  }
}

import { behavior } from 'receptor';

/**
 * Call a set of functions on a target in sequence.
 *
 * We used a named function here because we want it to inherit its lexical scope
 * from the behavior properties object, not from the module.
 *
 * @param {...Function} seq An array of functions that should be called on a
 *        target.
 */
const sequence = (...seq) => function callHooks(target = document.body) {
  seq.forEach((method) => {
    if (typeof this[method] === 'function') {
      this[method].call(this, target);
    }
  });
};

export class Behavior extends behavior {
  constructor(events, properties) {
    super(events, Object.assign({
      on: sequence('init', 'add'),
      off: sequence('teardown', 'remove')
    }, properties));
  }
}

import resolveIdRefs from 'resolve-id-refs';
import toggleFieldMask from './toggle-field-mask';

const CONTROLS = 'aria-controls';
const PRESSED = 'aria-pressed';
const SHOW_ATTR = 'data-show-icon';
const HIDE_ATTR = 'data-hide-icon';

/**
 * Replace the text 'fa-eye' with 'fa-eye-slash' for use in the password show
 * indicator's class list.
 *
 * @param {string} showIcon The name of the class which indicates that the icon
 *                 for "password shown" should be displayed.
 */
const getHideIconText = showIcon => showIcon.replace(/\bfa-eye\b/i, show => "fa-eye-slash");

/**
 * Component that decorates an HTML element with the ability to toggle the
 * masked state of an input field (like a password) when clicked.
 * The ids of the fields to be masked will be pulled directly from the button's
 * `aria-controls` attribute.
 *
 * @param  {HTMLElement} el    Parent element containing the fields to be masked
 * @return {boolean}
 */
 export default function toggleFormInput(el) {

   // this is the *target* state:
   // * if the element has the attr and it's !== "true", pressed is true
   // * otherwise, pressed is false
   const pressed = el.hasAttribute(PRESSED)
     && el.getAttribute(PRESSED) !== 'true';

   const fields = resolveIdRefs(el.getAttribute(CONTROLS));
   fields.forEach(field => toggleFieldMask(field, pressed));

   if (!el.hasAttribute(SHOW_ATTR)) {
     el.setAttribute(SHOW_ATTR, 'fa-eye');
   }

   const showIconName = el.getAttribute(SHOW_ATTR);
   const hideIconName = el.getAttribute(HIDE_ATTR) || getHideIconText(showIconName);

   el.classList.remove(pressed ? hideIconName : showIconName);
   el.classList.add(pressed ? showIconName : hideIconName);
   el.setAttribute(PRESSED, pressed);
   return pressed;
 }

/**
 * Flips given INPUT elements between masked (hiding the field value) and unmasked
 * @param {Array.HTMLElement} fields - An array of INPUT elements
 * @param {Boolean} mask - Whether the mask should be applied, hiding the field value
 */
 export default function toggleFieldMask(field, mask) {
   field.setAttribute('autocapitalize', 'off');
   field.setAttribute('autocorrect', 'off');
   field.setAttribute('type', mask ? 'password' : 'text');
 };

import { Behavior } from '../behavior';
import $ from 'jquery';

export class Username extends Behavior {
  constructor(prefix, options) {
    Username._prefix = prefix;
    Username._fieldMessageClass = options['fieldMessageClass'];
    Username._fieldMessageErrorClass = options['fieldMessageErrorClass'];
    Username._defaultValueMissingMessage = options['defaultValueMissingMessage'];

    // Remove any field error messages by setting them to display: none and
    // also remove any styling on the form field itself.
    $(`.${Username._fieldMessageClass}`).css('visibility', 'hidden');
    $(`.${Username._prefix}_usernameField`).removeClass(Username._fieldMessageErrorClass);

    super({
      ['change']: {
        [`.${Username._prefix}_usernameField`]: Username.changed
      }
    });
  }

  static changed(event) {
    let target = event.target;
    let ffSelector = `.${Username._prefix}_formFieldMessage`;
    let messageElement = $(target).siblings(ffSelector);
    Username.validate(target, messageElement);
  }

  static validate(element, messageElement) {
    if (element.validity.valueMissing) {
      if (messageElement.text().length === 0) {
        messageElement.setText(Username._defaultValueMissingMessage);
      }

      $(element).addClass(Username._fieldMessageErrorClass);
      $(messageElement).css('visibility', 'visible');
    } else {
      $(element).removeClass(Username._fieldMessageErrorClass);
      $(messageElement).css('visibility', 'hidden');
    }
  }
}

import { ErrorHandler } from './exceptions/ErrorHandler';
import { FieldValidator } from './validation/FieldValidator';
import { ComponentBehaviors } from './behavior';

export { ComponentBehaviors, ErrorHandler, FieldValidator };

import { PaulRevere } from '@foamfactory/paulrevere';
import $ from 'jquery';

/**
 * A messaging error handler for HTML forms.
 *
 * This class is a static class - it's not designed to be instantiated. Instead, its static methods
 * should be used within a page's dedicated Javascript to communicate UI error events.
 */
export class ErrorHandler {
  static processError(statusCode, errorMessage) {
    switch (statusCode) {
      case 401:
        if (errorMessage === "An invalid username or password was supplied as part of the Authorization header") {
          PaulRevere.getMessenger().displayErrorMessage('Invalid username or password');
        } else {
          PaulRevere.getMessenger().displayErrorMessage('You are not authorized to access that resource');
        }
        break;
      case 409:
        PaulRevere.getMessenger().displayErrorMessage(errorMessage);
        break;
      default:
        PaulRevere.getMessenger().displayErrorMessage('An error has occurred on our servers. If this persists, please contact support.');
        break;
    }
  }
}

import $ from 'jquery';

/**
 * Handler for validation of HTML form fields.
 *
 * Each form field to be validated should have a new instance of this class instantiated for it.
 */
export class FieldValidator {
  constructor (fieldElement) {
    this.element = fieldElement[0];
    this.valid = false;

    this.messageElement = $(this.element).closest('div').find('.form-field-error');
    if ($(this.element).attr('type') === 'password') {
      this.messageElement = $(this.element).parent().parent().find('.form-field-error');
    }

    // TODO_jwir3: Eventually, what we want to do is have this validator Object
    //             create the element in question, and have the message stored
    //             in a "data-" attribute.
    this.defaultMessage = $(this.messageElement).text();
    this.messageShown = false;
  }

  enable() {
    let self = this;

    self.element.addEventListener("blur", function(event) {
      self.validate();
    });
  }

  disable() {
    this.element.removeEventListener("blur");
    $(self.element).removeClass('interacted');
  }

  isValid() {
    return this.valid;
  }

  setOnValidityChangedHandler(handler) {
    let self = this;
    self.onValidityChanged = handler;
  }

  /**
   * Set a temporary message to be displayed when the validation fails.
   *
   * This will set a temporary message that will be displayed up until the next
   * time validation occurs.
   *
   * @param {String} message The temporary message to set
   */
  setMessage(message) {
    this.message = message;
    this.messageElement.text(this.message);
  }

  restoreDefaultMessage() {
    this.message = this.defaultMessage;
    this.messageElement.text(this.message);
  }

  showMessage() {
    this.messageShown = true;
    this.messageElement.show();
  }

  hideMessage() {
    this.messageShown = false;
    this.messageElement.hide();
  }

  setValidity(validity) {
    if (this.messageShown) {
      this.restoreDefaultMessage();
    }

    if (validity) {
      this.hideMessage();
      this.element.setCustomValidity('');
    } else {
      $(this.element).addClass('interacted');
      this.element.setCustomValidity(this.message);
      this.showMessage();
    }

    this._recordValidity(validity);
  }

  validate() {
    let self = this;
    let element = self.element;
    let messageElement = self.messageElement;
    let isValid = false;

    $(element).addClass('interacted');

    if (element.validity.valueMissing) {
      if (messageElement.text().length === 0) {
        self.defaultMessage = 'Please fill in this field';
      }

      self.setValidity(false);
      return;
    } else if (element.validity.typeMismatch) {
      if (messageElement.text().length === 0) {
        self.defaultMessage = 'This input does not appear valid';
      }

      self.setValidity(false);
    } else {
      isValid = true;
      let elementToMatch = $(element).data('mustmatch');
      if (elementToMatch) {
        let matchingElement = $('#' + elementToMatch);
        if (!matchingElement.val()) {
          // Element isn't input yet, so just report true for validity.
          isValid = true;
        } else {
          isValid = matchingElement.val() === $(element).val();
        }
      }

      self.setValidity(isValid);
    }
  }

  _recordValidity(isValid) {
    if (self.onValidityChanged && isValid !== self.valid) {
      self.valid = isValid;
      self.onValidityChanged(self.valid);
    }

    self.valid = isValid;
  }
}

import domready from 'domready';
import { Password } from './components/password';
import { Username } from './components/username';

const components = {
  // accordion,
  // banner,
  // footer,
  // navigation,
  "password": Password,
  "username": Username
  // search,
  // skipnav,
  // validator,
};

export class ComponentBehaviors {
  static init(prefix, options) {
    ComponentBehaviors._options = {
      "fieldMessageClass": `${prefix}_formFieldMessage`,
      "fieldMessageErrorClass": `${prefix}_formFieldErrorMessage`,
      "defaultValueMissingMessage": "Please fill in this field"
    };

    Object.assign(ComponentBehaviors._options, options);

    // TODO_jwir3: I think we could simplify this and make it a little easier to
    // read by creating an object with all of the default properties, and then
    // using object-assign to assign to the properties that are present in the
    // options parameter.

    // XXX_jwir3: Yup, this is hard to read. Basically, it's setting default
    //            values for options if they aren't provided.
    // ComponentBehaviors._options = {
    //   ...(options.fieldMessageClass && { fieldMessageClass: options.fieldMessageClass }) || { fieldMessageClass: `${prefix}_formFieldMessage` },
    //   ...(options.fieldMessageErrorClass && { fieldMessageErrorClass: options.fieldMessageErrorClass }) || { fieldMessageErrorClass: `${prefix}_formFieldErrorMessage` },
    //   ...(options.defaultValueMissingMessage && { defaultValueMissingMessage: options.defaultValueMissingMessage }) || { defaultValueMissingMessage: "Please fill in this field" },
    // };

    domready(() => {
      const target = document.body;
      Object.keys(components)
        .forEach((key) => {
          const behavior = new components[key](prefix, ComponentBehaviors._options);
          behavior.on(target);
        });
    });
  }

  static getOptions() {
    return ComponentBehaviors._options;
  }

  static getInstance(prefix, options) {
    if (!ComponentBehaviors._instances) {
      ComponentBehaviors._instances = [];
    }

    if (!(prefix in ComponentBehaviors._instances)
        || (!ComponentBehaviors._instances[prefix])) {
      ComponentBehaviors._instances[prefix]
        = ComponentBehaviors.init(prefix, options);
    }

    return ComponentBehaviors._instances[prefix];
  }
}
